sysauto_script.condition:
//Check to see if there are any active devices at the specified time.
//Return true to this condition if any active devices are found.
var laptopTickets = new GlideAggregate('x_casus_laptop_checkout_table');
laptopTickets.addAggregate('COUNT');
laptopTickets.addQuery('active', 'true');
laptopTickets.addQuery('state', 'Checked Out');
laptopTickets.query();
laptopTickets.next();
laptopTickets.getAggregate('COUNT') != '0'

sysauto_script.script:
var returnUtils = new ReturnUtils();
var now = new GlideDateTime();
var lateTicket = new GlideRecord('x_casus_laptop_checkout_table');
//Query for all active laptops that have a return time before the current time.
lateTicket.addActiveQuery();
lateTicket.addQuery('return_time', '<', now);
lateTicket.addQuery('state', 'Checked Out');
lateTicket.query();

while(lateTicket.hasNext()){
	lateTicket.next();

	if(returnUtils.isLate(lateTicket) == 1){
		var description = now.getDisplayValue() + ": " + lateTicket.device_name.device_type.device_type + " " + 
							lateTicket.device_name.device_id + " not returned.";

		returnUtils.assignHoldAndInvoice(lateTicket, description);

		var hold = new GlideRecord('x_casus_laptop_hold');
		hold.addActiveQuery();
		hold.addNotNullQuery('end_date');
		hold.addQuery('checkout_ticket', lateTicket.sys_id);
		hold.query();

		if(!hold.hasNext()){
			var holdID = returnUtils.createHold(lateTicket, description);
			hold.get(holdID);
			now.addMonthsUTC(6);
			hold.end_date = now;
			hold.update();
		}
	}
}
